(function() {

'use strict';

angular.module('app')
  .provider('grunticonEmbedConfig', grunticonEmbedConfig)
  .directive('grunticonEmbed', grunticonEmbed);

function grunticonEmbedConfig() {
  /* jshint validthis:true */
  this.gruntIcons = undefined;

  this.init = function(grunticonCss) {
    var css = window.document.querySelector( 'link[href$="'+ grunticonCss +'"]' );

    if (css) {
      this.gruntIcons = {};

      var icons = getIcons(css);
      Object.keys(icons).forEach(function(key) {
        var selector = key.slice('grunticon:.'.length);
        this.gruntIcons[selector] = icons[key];
      }, this);
    }
  };

  this.$get = function() {
    return this;
  };


  function getIcons(css) {
    var selectorPlaceholder = 'grunticon:';
    var icons = {},
      svgss,
      rules, cssText,
      iconClass, iconSVGEncoded, iconSVGRaw;

    svgss = css.sheet;
    if (!svgss) {
      return icons;
    }

    rules = svgss.cssRules ? svgss.cssRules : svgss.rules;
    for ( var i = 0; i < rules.length; i++ ) {
      cssText = rules[i].cssText;
      iconClass = selectorPlaceholder + rules[i].selectorText;
      iconSVGEncoded = cssText.split(');')[0].match( /US\-ASCII\,([^"']+)/ );

      if (iconSVGEncoded && iconSVGEncoded[1]) {
        iconSVGRaw = decodeURIComponent(iconSVGEncoded[1]);
        icons[iconClass] = iconSVGRaw;
      }
    }
    return icons;
  }
}

grunticonEmbed.$inject = ['grunticonEmbedConfig'];

function grunticonEmbed(grunticonEmbedConfig) {
  var directive = {
    restrict: 'A',
    scope: {},
    link: link
  };
  return directive;

  function link(scope, element, attrs) {
    var classes = attrs.class.split(' ');

    if (grunticonEmbedConfig.gruntIcons) {
      var icons = arrayIntersection(classes, Object.keys(grunticonEmbedConfig.gruntIcons));

      if (icons.length === 1) {
        element[0].innerHTML = grunticonEmbedConfig.gruntIcons[icons[0]];
        element[0].style.backgroundImage = 'none';
      }
    }
  }
}

function arrayIntersection(array1, array2) {
  return array1.filter(function(n) {
    return array2.indexOf(n) !== -1;
  });
}

})();

