'use strict';

function ErrorController ($scope, $window, $translate, $log, ibankSsoContant, error, securityService, timerService, commonService, localStorageService) {
	var errorCode = '9000';

	if(angular.isDefined(error.data) && error.data != null) {

		$log.error('[ErrorController: error]=' + JSON.stringify(error));

		if(angular.isDefined(error.data.errors && error.data.errors[0].title)) {
			errorCode = error.data.errors[0].title.split('-')[1];
		}

	} else if(angular.isDefined(error.errors) && error.errors != null) {

		$log.error('[ErrorController: error]=' + JSON.stringify(error));

		if(angular.isDefined(error.errors[0].title)) {
			errorCode = error.errors[0].title.split('-')[1];
		}

	} else
		$log.error('[ErrorController: name, message]=' + error.name + "," + error.message);
	
	$scope.error = {
		errorCode: errorCode
	};
	
	$translate.use(localStorageService.get('language')).then(function () {
		if ($translate.instant('error.message.' + errorCode) != 'error.message.' + errorCode) {
			$scope.errorCode = errorCode;
		} else {
			//if error message not found fallback to general error message
			$scope.errorCode = 'general';
		}
	});
	
	
	$scope.close = function() {
		if (securityService.getPreloginFlag() === 'N') {
            $log.debug('[CloseController: you are sso back to iBanking]');
            timerService.stopTimer();
            $window.location.href = ibankSsoContant.ibankSsoUrl;

        } else {
    		if (!commonService.getSourceUrl())
    			$window.location.href = ibankSsoContant.publicSiteUrl + commonService.getCountry();
    		else
    			$window.location.href = commonService.getSourceUrl();
    	}
	};
	
	$scope.retry = function() {	
		$window.location.reload();
	};
}

